mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT p.project_id, (SELECT COUNT(pp.member_id) FROM prj_particir pp where pp.project_id=p.project_id) mnumber, project_name, project_access, manager_accp, write_access, update_access, board_access, comment_access, file_access, dept_id, project_cls, particir.member_id, board.boardcount FROM project p LEFT JOIN( SELECT project_id, member_id, manager FROM prj_particir WHERE manager='A1' )particir ON (particir.project_id=p.project_id)
LEFT JOIN( SELECT project_id, COUNT(project_id) boardcount FROM prj_board GROUP BY project_id )board ON(p.project_id=board.project_id)
WHERE dept_id IN (SELECT dept_id FROM department WHERE company_id= #{companyId})) a ) WHERE rn BETWEEN #{pagingvo.start} AND #{pagingvo.end}
