mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* FROM ( SELECT p.project_id, (SELECT COUNT(pp.member_id) FROM prj_particir pp WHERE pp.project_id=p.project_id AND particir_accp='A1') mnumber, project_name, project_access, manager_accp, update_access, comment_access, file_access, dept_id, project_cls, particir.member_id, board.boardcount FROM project p LEFT JOIN( SELECT project_id, member_id, manager FROM prj_particir WHERE manager='A1' )particir ON (particir.project_id=p.project_id)
LEFT JOIN( SELECT project_id, COUNT(project_id) boardcount FROM prj_board GROUP BY project_id )board ON(p.project_id=board.project_id)
WHERE dept_id IN (SELECT dept_id FROM department WHERE company_id= #{companyId})) a ) WHERE rn BETWEEN #{pagingvo.start} AND #{pagingvo.end}

mapper.update:
		UPDATE 	company
	  		
	  			company_name = #{companyName}
	  			, company_url = #{companyUrl}
	  			
	  			, logo_path = #{logoPath}
	  			
	  			
	  			, real_logo_path = #{realLogoPath}
	  			 
	  			, company_addr = #{companyAddr}
  			
  		WHERE company_id = #{companyId}

update.foreach:
		 	UPDATE project
       		SET project_name = #{value}
       		WHERE project_id = #{key}

mapper.select:
SELECT project_name
FROM project 
WHERE dept_id=#{deptId}

mapper.update:
UPDATE prj_particir SET manager='A1' 
